import './App.css';
import { useState, useEffect, useRef } from "react";
import { usePDF } from 'react-to-pdf';
import axios from "axios";
import html2pdf from 'html2pdf.js';

import 'html2pdf.js';
// Ensure html2pdf library is included in your project

function generatePDF() {
  const content = document.querySelector('.story-container').innerHTML; // Adjust selector if necessary

  // Define CSS styles to apply within the PDF content
  const pdfStyles = `
    <style>
      body {
        font-family: 'Roboto', sans-serif;
        line-height: 1.6;
        padding: 20px;
        text-align: justify;
      }
      .content-wrapper {
        max-width: 600px;
        margin: 0 auto;
        padding: 20px;
        box-sizing: border-box;
      }
      h1, h2, h3 {
        text-align: center;
      }
      p {
        margin: 10px 0;
      }
    </style>
  `;

  // Create a temporary container for PDF generation with styles applied
  const pdfContent = `
    <div class="content-wrapper">
      ${pdfStyles}
      ${content}
    </div>
  `;

  // Use `html2pdf` to generate and download the PDF
  const options = {
      margin: 0.5, // Adjust if you want more space around the content
      filename: 'electronics-content.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 }, // Higher scale for better resolution
      jsPDF: { unit: 'in', format: 'a4', orientation: 'portrait' }
  };

  html2pdf()
    .set(options)
    .from(pdfContent)
    .save();
}


function App() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [domain, setDomain] = useState("VLSI");
  const { toPDF, targetRef } = usePDF({
    filename: 'electronics-content.pdf',
    options: { scale: 0.8 }
  });
  const [isLoading, setIsLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const textAreaRef = useRef();

  useEffect(() => {
    const savedResponse = localStorage.getItem("lastResponse");
    if (savedResponse) setResponse(savedResponse);
  }, []);

  useEffect(() => {
    if (response) localStorage.setItem("lastResponse", response);
  }, [response]);

  const submitHandler = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const res = await axios.post("http://localhost:3001/chat", {
        prompt: prompt,
        domain: domain
      });
      setResponse(res.data.response);
    } catch (error) {
      console.error("Error generating response:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopyToClipboard = () => {
    navigator.clipboard.writeText(response);
  };

  const handleClear = () => {
    setPrompt("");
    setResponse("");
    localStorage.removeItem("lastResponse");
  };

  return (
    <div className={`app-container ${darkMode ? 'dark-mode' : ''}`}>
      <aside className="sidebar">
        <h2 className="sidebar-title">Electronics Content Generator</h2>
        <div className="domain-switcher">
          <h3>Select Domain</h3>
          <select value={domain} onChange={(e) => setDomain(e.target.value)} className="domain-dropdown">
            <option value="VLSI">VLSI</option>
            <option value="Embedded Systems">Embedded Systems</option>
            <option value="Control Systems">Control Systems</option>
            <option value="Digital Systems">Digital Systems</option>
          </select>
        </div>
        <div className="dark-mode-toggle">
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
          </button>
        </div>
      </aside>

      <main className="main-content">
        <form onSubmit={submitHandler} className="form-container">
          <textarea
            name="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            className="prompt-input"
            placeholder="Write your electronics-related query..."
            maxLength={200}
            ref={textAreaRef}
          />
          <button type="submit" className="generate-button" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate Content'}
          </button>
        </form>

        {response && (
          <div className="content-container">
            <div className="content-display" ref={targetRef}>
              {response}
            </div>
            <div className="actions">
              <button onClick={toPDF} className="action-button">Download PDF</button>
              <button onClick={handleCopyToClipboard} className="action-button">Copy to Clipboard</button>
              <button onClick={handleClear} className="action-button clear-button">Clear</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
