import './App.css';
import { useState, useEffect, useRef } from "react";
import { useReactToPrint } from 'react-to-print';
import axios from "axios";

function App() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [domain, setDomain] = useState("VLSI");
  const [isLoading, setIsLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const contentRef = useRef(null);

  useEffect(() => {
    const savedResponse = localStorage.getItem("lastResponse");
    if (savedResponse) setResponse(savedResponse);
  }, []);

  useEffect(() => {
    if (response) localStorage.setItem("lastResponse", response);
  }, [response]);

  const submitHandler = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const res = await axios.post("http://localhost:3001/chat", {
        prompt: prompt,
        domain: domain
      });
      setResponse(res.data.response);
    } catch (error) {
      console.error("Error generating response:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePrint = useReactToPrint({
    content: () => contentRef.current,
    documentTitle: 'electronics-content',
    onBeforeGetContent: () => {
      // Check if the response exists and update the contentRef
      if (!response) {
        alert("No content available to print!");
        return Promise.reject(new Error("No content available to print"));
      }
      return Promise.resolve();
    },
    onAfterPrint: () => alert('PDF downloaded successfully!'),
  });

  const handleCopyToClipboard = () => {
    navigator.clipboard.writeText(response);
  };

  const handleClear = () => {
    setPrompt("");
    setResponse("");
    localStorage.removeItem("lastResponse");
  };

  return (
    <div className={`app-container ${darkMode ? 'dark-mode' : ''}`}>
      <aside className="sidebar">
        <h2 className="sidebar-title">Electronics Content Generator</h2>
        <div className="domain-switcher">
          <h3>Select Domain</h3>
          <select value={domain} onChange={(e) => setDomain(e.target.value)} className="domain-dropdown">
            <option value="VLSI">VLSI</option>
            <option value="Embedded Systems">Embedded Systems</option>
            <option value="Control Systems">Control Systems</option>
            <option value="Digital Systems">Digital Systems</option>
          </select>
        </div>
        <div className="dark-mode-toggle">
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
          </button>
        </div>
      </aside>

      <main className="main-content">
        <form onSubmit={submitHandler} className="form-container">
          <textarea
            name="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            className="prompt-input"
            placeholder="Write your electronics-related query..."
            maxLength={200}
          />
          <button type="submit" className="generate-button" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate Content'}
          </button>
        </form>

        {response && (
          <div className="content-container">
            <div className="content-display" >
              <h1>Electronics Content</h1>
              <p ref={contentRef}>{response}</p>
            </div>
            <div className="actions">
              <button onClick={handlePrint} className="action-button">Download PDF</button>
              <button onClick={handleCopyToClipboard} className="action-button">Copy to Clipboard</button>
              <button onClick={handleClear} className="action-button clear-button">Clear</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
