import './App.css';
import { useState, useEffect } from "react";
import { usePDF } from 'react-to-pdf';
import axios from "axios";
import { FacebookShareButton } from "react-share";

function App() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [theme, setTheme] = useState("Fantasy");
  const { toPDF, targetRef } = usePDF({
    filename: 'story.pdf',
    options: { scale: 0.8 } // Adjust scaling for readability
  });
  const [isLoading, setIsLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const pdfStyle = {
    width: "210mm",  // A4 page width
    padding: "20mm", // Ensure padding is applied to avoid clipping
    boxSizing: "border-box",
  };

  useEffect(() => {
    const savedResponse = localStorage.getItem("lastResponse");
    if (savedResponse) setResponse(savedResponse);
  }, []);

  useEffect(() => {
    if (response) localStorage.setItem("lastResponse", response);
  }, [response]);

  const submitHandler = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const res = await axios.post("http://localhost:3000/chat", {
        prompt: `${theme} theme: ${prompt}`
      });
      setResponse(res.data.response);
    } catch (error) {
      console.error("Error generating story:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const clearHandler = () => {
    setPrompt("");
    setResponse("");
    localStorage.removeItem("lastResponse");
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(response);
    alert("Copied to clipboard!");
  };

  return (
    <div className={`app-container ${darkMode ? 'dark-mode' : ''}`}>
      <aside className="sidebar">
        <h2 className="sidebar-title">Story Generator</h2>
        <div className="theme-switcher">
          <h3>Select Theme</h3>
          <select value={theme} onChange={(e) => setTheme(e.target.value)} className="theme-dropdown">
            <option value="Fantasy">Fantasy</option>
            <option value="Sci-Fi">Sci-Fi</option>
            <option value="Mystery">Mystery</option>
            <option value="Adventure">Adventure</option>
            <option value="Romance">Romance</option>
          </select>
        </div>
        <div className="dark-mode-toggle">
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
          </button>
        </div>
      </aside>

      <main className="main-content">
        <form onSubmit={submitHandler} className="form-container">
          <textarea
            name="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            className="prompt-input"
            placeholder="Write the opening lines of your story..."
            maxLength={200}
          />
          <button type="submit" className="generate-button" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate Story'}
          </button>
        </form>

        {isLoading && <div className="loading-spinner">Loading...</div>}

        {response && (
          <div className="story-container">
            <div className="story-content" ref={targetRef} style={pdfStyle}>
              {response}
            </div>
            <div className="actions">
              <button onClick={copyToClipboard} className="action-button">Copy</button>
              <button onClick={() => toPDF()} className="action-button">Download PDF</button>
              <button onClick={clearHandler} className="action-button">Clear</button>
              <FacebookShareButton
                url="https://www.facebook.com/"
                quote={response}
                hashtag="#StoryGenerator"
              >
                <div className="action-button">Share on Facebook</div>
              </FacebookShareButton>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
